maintenance.scss:
* {
    box-sizing: border-box;
}

body {
    -webkit-align-items: center;
    align-items: center;
    background: #888 url("../img/coming-soon.jpg") no-repeat center center;
    background-size: cover;

    -ms-box-orient: horizontal;
    color: #798289;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    -ms-flex-pack: space-around;
    font-family: Helvetica, Arial, Sans-Serif;
    font-size: 0.8em;
    height: 100vh;

    -webkit-justify-content: space-around;
    justify-content: space-around;
    line-height: 1.5em;
    margin: 0;
    min-height: 480px;
    min-width: 320px;
    padding: 20px 30px;
}

main {
    background-color: #fff;
    box-shadow: 0 2px 0 0 rgba(0, 0, 0, 0.07);
    display: block;
    padding: 20px 30px;
    text-align: center;
}

section {
    margin-bottom: 40px;
}

fieldset {
    border: 0;
    padding: 0;
}

legend {
    border: 0;
    clip: rect(0 0 0 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute !important;
    width: 1px;
}

label,
input {
    display: block;
    height: 30px;
    line-height: 30px;
    width: 100%;
}

label {
    color: #313b47;
    font-size: 14px;
    font-weight: bold;
    text-align: center;
}

input {
    -webkit-appearance: none;
    border-radius: 2px;
    border-style: solid;
    width: 100%;
}

input[type=text] {
    background-color: #f7f8f9;
    border: 1px solid #e1e6eb;
    margin-bottom: 1rem;
    padding: 0 1em;
}

input[type=submit] {
    background: #00abc9;
    border: 0;
    color: #fff;
    cursor: pointer;
    font-size: 12px;
    padding: 0 15px;
}

a {
    color: #00b9d3;
    margin: 0 5px;
    text-decoration: none;
}

h1 {
    color: #3c4043;
    font-size: 24px;
    font-weight: 300;
    margin-top: 15px;
}

div p {
    margin-bottom: 0;
    margin-top: 0;
}

.form-field--error {
    margin-bottom: 1rem;
}

.form-field--error input[type=text] {
    margin-bottom: 0;
}

.form-field--error .form-inlineMessage {
    color: #ac0005;
    margin-top: 0.375rem;
    width: 100%;
}

.powered-by {
    color: #acb3be;
    font-size: 11px;
    font-style: italic;
    line-height: 24px;
}

.powered-by a {
    background: url("../img/bc_logo_sprite.png") 0 -75px no-repeat;
    display: inline-block;
    height: 22px;
    opacity: 0.7;
    text-indent: -9999px;
    width: 95px;
}

.powered-by a:hover {
    opacity: 1;
}
@media screen and (min-width: 540px) {
    body {
        font-size: 1em;
    }

    main {
        margin: 0 auto;
        padding: 45px 60px;
        width: 500px;
    }

    label,
    input {
        display: inline-block;
        margin-right: 10px;
        width: auto;
    }

    label {
        text-align: left;
    }

    input[type=text] {
        margin-bottom: 0;
        margin-right: 10px;
    }

    .form-field {
        display: inline-block;
        position: relative;
    }

    .form-field--error .form-inlineMessage {
        left: 100px;
        position: absolute;
        top: 30px;
    }
}

assets/scss/layouts/products/_productGri:
// =============================================================================
// PRODUCT GRID (CSS)
// =============================================================================


// Product grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .products class.
//
// -----------------------------------------------------------------------------

.productGrid {

    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin-bottom: spacing("single");

    .product {
        @include grid-column(6, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;

        @include breakpoint("small") {
            width: grid-calc(5, $total-columns);
        }

        @include breakpoint("medium") {
            width: grid-calc(3, $total-columns);
        }
    }
}


// Product when sidebar is present
// -----------------------------------------------------------------------------
//
// 1. When the sidebar is visible, reduce products to 3 across.
//
// -----------------------------------------------------------------------------

.page-sidebar + .page-content {
    .productGrid {
        .product { // 1
            @include breakpoint("small") {
                width: grid-calc(5, $total-columns);
            }
        }
    }

}


//
// Product Grid with masonry layout
//
// Purpose: Display products in a masonry grid, with a display inline-block for
// older browsers that don't support css multicolumn
//
// -----------------------------------------------------------------------------

.productMasonry {
    column-count: 2;
    column-fill: auto;
    column-gap: remCalc(20px);
    margin: 0 0 spacing("single");
    overflow: hidden;

    @include breakpoint("small") {
        column-count: 3;
    }

    @include breakpoint("medium") {
        column-count: 4;
    }

    .product {
        break-inside: avoid;
        display: block;
        margin-bottom: spacing("double");
        padding: 0;
        page-break-inside: avoid; // for firefox
        width: 100%;
    }

    .card {
        margin: 0;
    }
}


// Product grid view variation
// -----------------------------------------------------------------------------

.productGrid--maxCol6 {
    .no-csscolumns & {
        .product {
            @include breakpoint("medium") {
                width: grid-calc(2, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            column-count: 6;
        }
    }
}

.productGrid--maxCol3 {
    .no-csscolumns & {
        .product {
            @include breakpoint("medium") {
                width: grid-calc(5, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            @include grid-column(12);

            float: none;

            .product {
                @include grid-column(4, $float: none);
            }
        }
    }
}
